#nombre des journalistes
def nombre_journalist(journalist)
  puts "Le nombre des journalistes que nous avons est :#{journalist.length}"
end

#Handle contient un numero
def contient_numero(journalist)
  handle_chiffre = Array.new()
  handle_chiffre = journalist.select{|v| v =~ /[123456789]/}
  puts "les handles qui contiennent un numéro sont : #{handle_chiffre.length}"
end

#Quatre lettre "Aude"
def quatre_lettre(journalist)
  quatre_lettre = Array.new()
  quatre_lettre = journalist.grep(/[Aa]ude/)
  puts "Il existe #{quatre_lettre.length}"+' handles  qui contiennent les 4 lettres du prénom "Aude" à la suite'
end

#Majuscule après @
def majuscule_a(journalist)
  majuscule = Array.new()
  majuscule = journalist.select{|v| v =~ /@[A-Z]/}
  puts "Il y a #{majuscule.length} handles qui commencent par un majuscule après @"
end

#Contiennent une majuscule
def majuscule_b(journalist)
  maj = 0
  for n in journalist
    if n.scan(/[A-Z]/).length == 1
      maj = maj + 1
    end
  end
  puts "Il y a #{maj} handles qui contiennent une majuscule"
end

#Underscore
def underscore(journalist)
  under = 0
  for n in journalist
    under = under + n.scan(/_/).length
  end
  puts "Il y a #{under} underscore dans tous les pseudos confondus"
end

#par ordre alphabetique
def alphabetique(journalist)
  alpha = journalist.sort_by{|a| a.downcase}
  puts alpha
end

#50 handles les plus courts
def plus_courts(journalist)
  court = journalist.sort_by(&:length)
  puts court[0..49].inspect
end

# la position dans l'array de la personne @epenser
def position(journalist)
  puts "La position de @epenser dans l'array est #{journalist.index("@epenser")}"
end

#menu
def menu(journalist)
  puts "Pour le nombre des journalistes dans array, tapez 1"
  puts "Pour les handles qui contiennent un numero, tapez 2"
  puts 'Pour les handles qui contiennent les 4 lettres du prénom "Aude" à la suite, tapez 3'
  puts "Qui commencent par une majuscule (première lettre juste après le @) , tapez 4"
  puts "Qui contiennent une majuscule , tapez 5"
  puts "Pour les underscores _ dans tous les pseudos confonduse , tapez 6"
  puts "Pour trier la liste de handle par ordre alphabétique , tapez 7"
  puts "Pour les 50 handles les plus courts de ce array , tapez 8"
  puts "Pour les 50 handles les plus courts de ce array , tapez 9"
  print ">"
  choix = gets.chomp.to_i
  while choix > 9
    puts "Seulement de 1 à 9 svp"
      choix = gets.chomp.to_i
  end
  if choix == 1
    nombre_journalist(journalist)
  elsif choix == 2
    quatre_lettre(journalist)
  elsif choix == 3
    contient_numero(journalist)
  elsif choix == 4
    majuscule_a(journalist)
  elsif choix == 5
    majuscule_b(journalist)
  elsif choix == 6
    underscore(journalist)
  elsif choix == 7
    alphabetique(journalist)
  elsif choix == 8
    plus_courts(journalist)
  elsif choix == 9
    position(journalist)
  end

end
menu(journalist)
